package com.javaex.basic.loop;

public class ForEx3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int r=6;
		
		int x,y;
		for(x=1; x<=r; x++) {
		
			for(y=1; y<=x; y++) {
				System.out.print("*");
			}
			System.out.println();		
		
				
			}
	}

}