package com.javaex.basic.loop;

public class WhileEx3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int r=6;
	int x=1;
	
		while(x<r) {
			
			int y=1;
		while(y<=x) {
		
		System.out.print("*");
		y ++;
		}
		System.out.println();
		x ++;
			
			}
	}}


